package com.example.UserApi.controller;import com.example.UserApi.dto.UserDTO;import com.example.UserApi.model.Comment;import com.example.UserApi.model.Post;import com.example.UserApi.model.User;import com.example.UserApi.service.FetchService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.stream.Collectors;@RestController@Slf4j@RequestMapping(name = "Query Users", value = "/api/v1")public class UserController {    @Autowired    private FetchService fetchService;    @GetMapping("/fetchUsers")    public List<UserDTO> fetchUsers(){        List<UserDTO> users = fetchService.fetchUsers().stream().map(UserDTO::new).collect(Collectors.toList());        return users;    }    @GetMapping("/fetchPosts")    public List<Post> fetchPosts(){        List<Post> posts = fetchService.fetchPosts();        return posts;    }    @GetMapping("/fetchComments")    public List<Comment> fetchComments(){        List<Comment> comments = fetchService.fetchComments();        return comments;    }}